package com.nikitha.oyelabstask;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import androidx.viewpager.widget.ViewPager;

import android.os.Bundle;

import com.smarteist.autoimageslider.SliderView;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {

    // Urls of our images.
    String url1 = "data:image/jpeg;base64,";
    String url2 = "data:image/jpeg;base64,";
    String url3 = "data:image/jpeg;base64,";


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        // we are creating array list for storing our image urls.
        ArrayList<SliderData> sliderDataArrayList = new ArrayList<>();

        // initializing the slider view.
        SliderView sliderView = findViewById(R.id.slider);

        // adding the urls inside array list
        sliderDataArrayList.add(new SliderData(url1));
        sliderDataArrayList.add(new SliderData(url2));
        sliderDataArrayList.add(new SliderData(url3));

        // passing this array list inside our adapter class.
        SliderAdapter adapter = new SliderAdapter(this, sliderDataArrayList);

        // below method is used to set auto cycle direction in left to
        // right direction you can change according to requirement.
        sliderView.setAutoCycleDirection(SliderView.LAYOUT_DIRECTION_LTR);

        // below method is used to
        // setadapter to sliderview.
        sliderView.setSliderAdapter(adapter);

        // below method is use to set
        // scroll time in seconds.
        sliderView.setScrollTimeInSec(3);

        // to set it scrollable automatically
        // we use below method.
        sliderView.setAutoCycle(true);

        // to start autocycle below method is used.
        sliderView.startAutoCycle();

        ArrayList<ItemModel> itemModelArrayList = new ArrayList<>();
        ItemModel[] itemModels = new ItemModel[5];
        String []images = {"R.drawable.banana","R.drawable.banana", "R.drawable.giftcard", "R.drawable.giftcard","R.drawable.giftcard"};
        String []name ={"Organic bananas","Red Apples","Organic grapes","Strawberries","Oranges"};
        String []quantity = {"7pcs, priceg","9pcs, priceg","8pcs, priceg","5pcs, priceg","9pcs, priceg"};
        String []price = {"4.99","6.99","9.99","10.99","3.99"};

        for(int i=0;i<5;i++)
        {
            itemModels[i] = new ItemModel();
            itemModels[i].image=images[i];
            itemModels[i].quantity=quantity[i];
            itemModels[i].price=price[i];
            itemModels[i].name=name[i];
            itemModelArrayList.add(itemModels[i]);
        }

        LinearLayoutManager layoutManager = new LinearLayoutManager(this);
        ItemAdapter adapter1 = new ItemAdapter(this, itemModelArrayList);
        RecyclerView recyclerView = findViewById(R.id.rvExOff);
        layoutManager.setOrientation(LinearLayoutManager.HORIZONTAL);
        recyclerView.setLayoutManager(layoutManager);
        recyclerView.setAdapter(adapter1);
        adapter1.notifyDataSetChanged();

        ArrayList<ItemModel> itemModelArrayList1 = new ArrayList<>();
        ItemModel[] itemModels1 = new ItemModel[5];
        String []grocName ={"Pulses","Cereals","Oils","Flours","Beverages"};

        for(int i=0;i<5;i++)
        {
            itemModels1[i] = new ItemModel();
            itemModels1[i].name=grocName[i];
            itemModelArrayList1.add(itemModels1[i]);
        }

        GroceryAdapter adapter2 = new GroceryAdapter(this, itemModelArrayList1);
        RecyclerView groceryRV =findViewById(R.id.rvGroceries);
        LinearLayoutManager layoutManager1 = new LinearLayoutManager(this);
        layoutManager1.setOrientation(LinearLayoutManager.HORIZONTAL);
        groceryRV.setLayoutManager(layoutManager1);
        groceryRV.setAdapter(adapter2);
        adapter2.notifyDataSetChanged();

        ArrayList<ItemModel> itemModelArrayList2 = new ArrayList<>();
        ItemModel[] itemModels2 = new ItemModel[5];
        String []bestSellName ={"Bell Pepper Red","Ginger","Organic grapes","Strawberries","Raw rice"};

        for(int i=0;i<5;i++)
        {
            itemModels2[i] = new ItemModel();
            itemModels2[i].image=images[i];
            itemModels2[i].quantity=quantity[i];
            itemModels2[i].price=price[i];
            itemModels2[i].name=bestSellName[i];
            itemModelArrayList2.add(itemModels2[i]);
        }

        ItemAdapter adapter3 = new ItemAdapter(this, itemModelArrayList2);
        RecyclerView rvBestSell =findViewById(R.id.rvBestSell);
        LinearLayoutManager layoutManager2 = new LinearLayoutManager(this);
        layoutManager2.setOrientation(LinearLayoutManager.HORIZONTAL);
        rvBestSell.setLayoutManager(layoutManager2);
        rvBestSell.setAdapter(adapter3);
        adapter3.notifyDataSetChanged();
    }
}